<?php if ($block->text()->isNotEmpty()): $textFill = $block->textFill()->toStructure()->first(); ?>
  <?php if ($block->label()->isNotEmpty() && $block->labelPosition()->value() === 'top'): ?><span class="title-label<?php if ($block->labelStyle()->value() === 'tag'): ?> margin-auto-<?= $block->alignContent() ?> tag tag-size-sm tag-style-new<?php endif ?>"><?= $block->label() ?></span><?php endif ?>
  <<?= $level = $block->level()->or('h2') ?> class="<?php if ($block->label()->isNotEmpty() && $block->labelPosition()->value() === 'bottom'): ?>space-bottom-none <?php endif ?>title-fill-<?php if ($textFill && (($textFill->type()->value() === 'color' && $textFill->color()->isNotEmpty()) || ($textFill->type()->value() === 'gradient' && $textFill->gradient()->isNotEmpty()))): ?><?= $textFill->type() ?><?php else: ?>none<?php endif ?> title-<?= $block->fontSize() ?>"><?= $block->text() ?></<?= $level ?>>
  <?php if ($block->label()->isNotEmpty() && $block->labelPosition()->value() === 'bottom'): ?><span class="title-label<?php if ($block->labelStyle()->value() === 'tag'): ?> margin-auto-<?= $block->alignContent() ?> tag tag-size-sm tag-style-new<?php endif ?>"><?= $block->label() ?></span><?php endif ?>
<?php endif ?>
